<?php// application/forms/Codesearch.phpclass Default_Form_Codesearch extends Zend_Form{    public function init()    {        // Set the method for the display form to POST        $this->setMethod('post');        // Add the search input element        $this->addElement('text', 'search', array(            'label'      => 'Code search:',            'required'   => true,        ));                // Add the submit button        $this->addElement('submit', 'submit', array(            'ignore'   => true,            'label'    => 'Search',        ));        // And finally add some CSRF protection        $this->addElement('hash', 'csrf', array(            'ignore' => true,        ));    }}