<?php// application/models/Codesearch.phpclass Default_Model_Codesearch{    protected $_client;    protected $_gdata;    protected $_query;    protected $_package;    protected $_search;    protected $_feed;        public function __construct(array $form = null)    {        $this->_client = new Zend_Http_Client();        $this->_client->setHeaders('GData-Version', '2');        $this->_gdata = new Zend_Gdata($this->_client);        $this->_query = new Zend_Gdata_Query('http://www.google.com/codesearch/feeds/search');        $this->_package = 'package:.*svn\.magentocommerce';                if (is_array($form)) {            $this->_search = strip_tags($form['search']);        }    }        public function getPackage()    {        return $this->_package;    }    //public function search()    public function search()    {        try {            $this->_query->setQuery($this->_package . ' ' . $this->_search);            $this->_query->setMaxResults(50);            $this->_feed = $this->_gdata->getFeed($this->_query);                        return $this->_feed;        }        catch (Zend_Gdata_App_HttpException $httpException) {            exit("An error occurred trying to connect to the proxy server\n" . $httpException->getMessage() . "\n");        }    }}